            String geboorte = spelerDialog.getTxtGeboorteDatum().getText();
            String postcode = spelerDialog.getTxtPostcode().getText();
            String adres = spelerDialog.getTxtAdres().getText();
            String geslacht = spelerDialog.getTxtGeslacht().getText();
            String telefoon = spelerDialog.getTxtTelefoon().getText();

