            String postcodeRegex = "(NL-)?(\\d{4})\\s*([A-Z]{2})";
            String geboorteRegex = "^((19|2[0-9])[0-9]{2})-(0[1-9]|1[012])-(0[1-9]|[12][0-9]|3[01])$";
